dynamideForm.event:

Designer designer = event.sender.getObject();
if ( designer == null ) {
    System.out.println("ERROR: [designertop.xml] designerObject not found in page.getObject()");
} else {
    event = designer.onAction(event);
}


System.out.println(".......... designertop_onLoad ........");
Designer designer = new Designer();
Page page = event.sender;
page.setObject(designer);
